import React, { useEffect, useState } from 'react'
import './PostData.css'

const PostData = () => {

  const[post,setPost]=useState([])



 useEffect(()=>{

  const postData=async () => {
    const data=await apiCall();
    setPost(data)
  };

  postData();

 },[])




  async function apiCall() {

    const URL="https://gorest.co.in/public/v2/posts"

    try{
      const response= await fetch(URL);
      if(!response.ok){
        throw new Error(`Eror: ${response.status}`);
      }

      return await response.json();
    }
    catch(error){
      console.log("failed to fetch posts: ",error)
      return []
    }
    
  }

  
  return (
    <div className='outDat'>
        <div className='inData'>

            <div className='postCont'>
                <div className='pno'>post no 1</div>
                <div className='ptitle'>Title</div>
                <div className='pbody'>Lorem ipsum dolor sit amet consectetur adipisicing elit. Ad quis doloremque repellendus quisquam aliquam, incidunt magni voluptatem, veniam est, dolores quos tenetur! Hic atque alias doloremque aliquam minima pariatur non temporibus dolores enim quia in, magni veniam vel odio est tempore molestias fugiat, dolorum commodi sequi ducimus, molestiae repellat! Voluptate.</div>
                <div className='pauthor'>Id no</div>
            </div>
            
        </div>
    </div>
  )
}

export default PostData
