import React, { useState } from 'react'
import './FormPart.css'

const FormPart = () => {


    const [nameBox, setNameBox]=useState("Enter ur name")
    const [clgBox, setClgBox]=useState("enter ur college")



    // to handle name change event from form

    const handleNameChange=(event)=>{
        console.log(event.target.value)
        setNameBox(event.target.value)
    }

    // to handle college name change event from form

    const handleClgChange=(event)=>{
        console.log(event.target.value)
        setClgBox(event.target.value)
    }



    const nameList=[
        {
            stname:"Siva",
            college:"Lmcst"
        },

    ]



  return (
    <div className='outerForm'>
        <div className='innerForm'>
            <div className='dataEntry'>
                <form >
                    <label htmlFor="name" className='lb'>Name </label>
                    <input type="text"  value={nameBox} className='inp' onChange={handleNameChange}/>
                    <label htmlFor="college" className='lb'>College </label>
                    <input type="text"  value={clgBox} className='inp'/>
                    <button className='okbtn' >Add</button>
                </form>
            </div>

            <div className='tableDiv'>
                <table>
                    <thead>
                        <tr>
                            <th>Serial No</th>
                            <th>Name</th>
                            <th>College</th>
                        </tr>
                    </thead>

                    <tbody>

                        {nameList.map((ele,idx)=>{
                            return (
                                <tr key={idx}>
                                    <td>{idx+1}</td>
                                    <td>{ele.stname}</td>
                                    <td>{ele.college}</td>
                                </tr>
                            )
                        })}





                    </tbody>
                </table>
            </div>

        </div>
    </div>
  )
}

export default FormPart
