    const getDataFromChild=(data)=>{
        console.log(data);
        setData(data);
    }

    const dataFromSibling=getData;

  return (
    <div className='main-content'>
    <SideBar dp={getDataFromChild}/>
    <DataPart dp2={dataFromSibling}/>
  </div>
  )
}

export default MainContent
