    const handleOnChange=(data,key)=>{
        console.log(data,key)
        let temp={...details}
        temp[key]=data
        setDetails(temp)
    }


    const handleEdit=async(id)=>{
        setEditDet(true);
        try {
            const editData = await getSingleUser(id); // Fetch data from the API
            if (editData) {
                setDetails(editData); // Update the state with the fetched data
                console.log("Updated details:", editData.name); // Log the updated name
            }
        } catch (error) {
            console.error("Error fetching data:", error);
        }

    }

    // const apiEdit=async (data)=>{

