import axios from "axios";
const BASE_URL="https://gorest.co.in/public/v2/";
const api_key='35c707c450cba2d92a0573f66a364f42b92af1cf2e2a1e260034a0b0944ff4c6';


const axiosInstance=axios.create({
    baseURL:BASE_URL,
    // headers:{
    //     'Content-Type':'Application/json',
    //     'Authorization':'Bearer 35c707c450cba2d92a0573f66a364f42b92af1cf2e2a1e260034a0b0944ff4c6'}
})




// Add a request interceptor
axiosInstance.interceptors.request.use(
  (config) => {
    // You can modify the request here before it's sent
    console.log('Request Interceptor:', config);
    config.headers['Content-Type']='application/json'
    config.headers['Authorization']=`Bearer ${api_key}`

    // Example: Modify headers if needed (e.g., add a custom header or refresh token)
    // config.headers['Custom-Header'] = 'CustomValue';
    return config;
  },
  (error) => {
    // Handle request error (optional)
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);




export const API=axiosInstance;
