import React, { useContext } from 'react'
import { UserContext } from './Component1'

const Component4 = () => {

  const {name,age,updateName}=useContext(UserContext)

    
    

  return (
    <div>
      Component4 {name}
      <div><input type="text" onChange={(e)=>{updateName(e.target.value)}}/></div>
      {/* <div><input type="text" name="" id=""/></div> */}
    </div>
  )
}

export default Component4
