import React, { useEffect, useRef, useState } from 'react'

const ComponentForuseRef = () => {

    const ref=useRef(0);
    const [val,setVal]=useState('')

    const focusRef=useRef();

    const prevRef=useRef('');

    const handleFocus=()=>{
        focusRef.current.focus();
    }


    useEffect(()=>{

        prevRef.current=val

    },[val])

    useEffect(()=>{

        ref.current+=1

    })

  return (
    <div>
        <h1>{ref.current}</h1>
        
        <input type="text" onChange={(e)=>{setVal(e.target.value)}} ref={focusRef} />
        <button onClick={handleFocus}>click me to focus</button>
        <h1>previous value : {prevRef.current}</h1>
        <h1>current value : {val}</h1>
        
    </div>
  )
}

export default ComponentForuseRef
