import React, { useEffect, useRef, useState } from 'react'

const ComponentForuseRef = () => {

    const ref=useRef(0);
    const [val,setVal]=useState('')

    useEffect(()=>{

        ref.current+=1

    })

  return (
    <div>
        <h1>{ref.current}</h1>
        <input type="text" onChange={(e)=>{setVal(e.target.value)}}/>
    </div>
  )
}

export default ComponentForuseRef
